import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import { HeaderArea } from './styled';
import { Button } from '../Menubar/Button';
import Dropdown from '../Menubar/Dropdown';

import '../Menubar/Navbar.css'

import { isLogged, doLogout } from '../../../helpers/AuthHandler';

const Header = () => {
    let logged = isLogged();

    const handleLogout = () => {
        doLogout();
        window.location.href = '/';
    }

    const [click, setClick] = useState(false);
    const [dropdown, setDropdown] = useState(false);

    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    const onMouseEnter = () => {
        if(window.innerWidth < 960) {
            setDropdown(false);
        } else {
            setDropdown(true);
        }
    };

    const onMouseLeave = () => {
        if(window.innerWidth < 960) {
            setDropdown(false);
        } else {
            setDropdown(false);
        }
    };

    

    return (
        <HeaderArea>
            <div className="container">
                <div className="logo">
                    <Link to="/">
                        <span className="logo-1">ReGep</span>
                    </Link>
                </div>
                <nav>
                    <ul>
                        {logged && //Se tiver logado
                            <>
                                <li>
                                    <Link to="/my-account">Minha conta</Link>
                                </li>
                                <li>
                                    <button onClick={handleLogout}>Sair</button>
                                </li>
                                <li className='nav-item' onClick={handleClick}>
                                    <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
                                    {dropdown && <Dropdown />}
                                </li>
                                <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                                    <li className='nav-item'
                                        onMouseEnter={onMouseEnter}
                                        onMouseLeave={onMouseLeave}
                                    >
                                        <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                                            InÃ­cio
                                        </Link>

                                    
                                        <Link to='/Tasks' className='nav-links' onClick={closeMobileMenu}>
                                            Tarefas
                                        </Link>
                                    </li>
                                </ul>
                            </>
                        } 
                        {!logged && // se nÃ£o tiver logado
                            <>
                                <li>
                                    <Link to="/signin">Login</Link>
                                </li>
                                <li>
                                    <Link to="/signup">Cadastrar</Link>
                                </li>
                            </>
                        }
                    </ul>
                </nav>
            </div>
        </HeaderArea>
    );
}

export default Header;
