import React, { useState } from 'react';
import { PageArea } from './styled';
import useApi from '../../helpers/OlxAPI';
import { doLogin } from '../../helpers/AuthHandler';

import { PageContainer, PageTitle } from '../../components/MainComponents'

const Page = () => {
    const api = useApi();

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [rememberPassword, setRememberPassword] = useState(false);
    //disabled quando a pessoa estiver esperando carregar o login, enquanto isso ela não pode preencher o formulário
    const [disabled, setDisabled] = useState(false);
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setDisabled(true);

        const json = await api.login(email, password);

        if(json.error) {
            setError(json.error);
        } else {
            //Salva o cookie, carrega toda a página e direciona pro "home"
            doLogin(json.toke, rememberPassword);
            window.location.href = '/';
        }
    }

    return (
        <PageContainer>
            <PageTitle>Login</PageTitle>
            <PageArea>
                <form onSubmit={handleSubmit}>
                    <label className="area">
                        <div className="area--title">Email</div>    
                        <div className="area--input">
                            <input type="email" disabled={disabled} />  
                        </div>    
                    </label>  
                    <label className="area">
                        <div className="area--title">Senha</div>    
                        <div className="area--input">
                            <input type="password" disabled={disabled} />  
                        </div>    
                    </label>  
                    <label className="area">
                        <div className="area--title">Lembrar Senha</div>    
                        <div><input type="checkbox" disabled={disabled} /></div>    
                    </label>   
                    <label className="area">
                        <div className="area--title"></div>    
                        <div className="area--input">
                            <button disabled={disabled}>Fazer Login</button>  
                        </div>    
                    </label>        
                </form>
            </PageArea>
        </PageContainer>
    );
}

export default Page;
