import styled from 'styled-components';

export const HeaderArea = styled.div`
    background-color: #FFF;
    height: 60px;
    border-bottom: 1px solid #CCC

    .container {
        max-width: 1000px;
        margin: auto;
        display: flex;
    }

    .logo img {
        flex: 1;
        display: flex;
        align-items:center;
    }
`;
